mapper.update:
update goodx_topics set
	topic_views		= topic_views + 1
where id = #{id}

update goodx_topics set
	topic_collections	= topic_collections + 1
where id = #{id}

update goodx_topics set
	topic_likes			= topic_likes + 1
where id = #{id}

update goodx_topics set
	topic_replies 		= topic_replies + 1
where id = #{id}

update goodx_topics set
	topic_replies 		= topic_replies - 1
where id = #{id}
