mapper.update:
update goodx_users set
	deleted			= 1
where id = #{id}

update goodx_users set
	user_active		= 1
where id = #{id}

update goodx_users set
	user_topics		= user_topics + 1
where id = #{id}

update goodx_users set
	user_replies 	= user_replies + 1
where id = #{id}

update goodx_users set
	user_replies	= user_replies - 1
where id = #{id}

update goodx_user set
	username		= #{userName}
where id = #{id}

mapper.select:
select username 
from goodx_users
where email = #{email}
